// Convierte una fecha de Excel (ej. 46017) al formato YYYY-MM-DD
const excelToLocale = (value) => {
  if (!value) return null;

  // Si el valor ya viene como texto tipo "2025-12-25" o "25/12/2025", lo dejamos igual
  if (typeof value === 'string' && (value.includes('/') || value.includes('-'))) {
    return value;
  }

  // Si viene como n√∫mero (Excel date)
  const excelEpoch = 25569; // 1 Jan 1970 - 1 Jan 1900 offset
  const millis = (value - excelEpoch) * 86400 * 1000;

  return new Date(millis).toISOString().split('T')[0];
};

