// Creamos un nuevo arreglo manteniendo la posici√≥n de Excel
return excelItems.map((excelItem, index) => {
  // Si existe un resultado en Monday con el mismo √≠ndice, lo usamos; si no, usamos un objeto vac√≠o
  const mondayItem = mondayItems[index]?.json || {};

  // Verificamos si existe un ID Venue en Monday (seg√∫n los column_values)
  const idVenueMonday = mondayItem?.column_values?.find(c => c.id === "id_venue")?.text?.trim() || "";

  // Si hay texto en ese campo, significa que ya existe en Monday
  const existeEnMonday = idVenueMonday !== "";

  return {
    json: {
      ...excelItem.json,   // Datos originales del Excel
      monday: mondayItem,  // Resultado de Monday (vac√≠o si no existe)
      existeEnMonday       // true si ya existe, false si no
    }
  };
});

